import * as React from "react";

function PageHeader() {
	const bannerRef = React.useRef(null);
	const lastScroll = React.useRef(0);

	React.useEffect(() => {
		const handleScroll = () => {
		  const currentScroll = window.scrollY;

		  if (currentScroll > lastScroll.current) {
			// Scrolling down
			bannerRef.current.style.transform = 'translateY(-100%)';
		  } else {
			// Scrolling up
			bannerRef.current.style.transform = 'translateY(0)';
		  }

		  lastScroll.current = currentScroll;
		};

		window.addEventListener('scroll', handleScroll);

		return () => {
		  window.removeEventListener('scroll', handleScroll);
		};
	  }, []);

	return (
		<section ref={bannerRef} id="page-header" className="fixed top-0 left-1/2 transform -translate-x-1/2 w-[90%] bg-transparent text-white text-center py-4 px-6 rounded-xl shadow-2xl z-50">
			<header className="flex flex-row items-center">
				<h1 className="pl-8 text-4xl whitespace-nowrap font-serif">Drew Bowman </h1>

				<nav className="nav-header">
					<HeaderLink href="#contact-info">Contact Information</HeaderLink>
					<HeaderLink href="#education">Education</HeaderLink>
					<HeaderLink href="#experience">Experience</HeaderLink>
					<HeaderLink href="#projects">Projects</HeaderLink>
					<HeaderLink href="#skills">Skills</HeaderLink>
					<HeaderLink href="#honors-and-awards">Honors and Awards</HeaderLink>
				</nav>
			</header>
		</section>
	);

}

const HeaderLink = ({ href, children }) => {
	return <a className="text-xl p-1 m-2 rounded hover:drop-shadow-lg hover:backdrop-brightness-95" href={href}>{children}</a>;
}

export default PageHeader;
