function ImageSection() {

	return (
		<section className="relative w-full">
			<img className="w-full h-full object-cover rounded" src="Profile_Pic_Wide.jpg" alt="Drew Bowman"></img>
			<div class="absolute top-0 right-0 h-full flex items-center pr-8">
      			<div class="bg-lime-800 bg-opacity-75 text-white p-4 rounded-md">
	  				<h3 
						className="opacity-100 text-base md:text-lg lg:text-2xl text-white font-serif"
					>
						<strong>Hi, I'm Drew Bowman.</strong> <br/>
						I'm a software engineer <br/> 
						helping businesses create <br/> 
						beautiful and functional websites.
					</h3>
				</div>
			</div>
		</section>
	);
}

export default ImageSection;
