import { useEffect } from 'react';
import AOS from 'aos';
import 'aos/dist/aos.css';

const accomplishments = [
  {
    items: [
      'Completed 100+ hours of coding challenges',
      'Built a React portfolio',
      'Learned TypeScript',
    ],
    image: '/images/code-icon.png',
  },
  {
    items: [
      'Contributed to 3 open-source projects',
      'Fixed 10+ GitHub issues',
      'Opened 2 PRs',
    ],
    image: '/images/github-icon.png',
  },
  {
    items: [
      'Earned 5-star Freelancer reviews',
      'Delivered 10+ client projects',
      'Improved SEO for blogs',
    ],
    image: '/images/star-icon.png',
  },
  {
    items: [
      'Won a hackathon',
      'Published 3 npm packages',
      'Mentored junior devs',
    ],
    image: '/images/trophy-icon.png',
  },
];

export default function AlternatingCards() {
  useEffect(() => {
    AOS.init({ duration: 800, once: true });
  }, []);

  return (
    <div className="p-8 space-y-12 bg-gray-100 min-h-screen">
      <h1 className="text-3xl font-bold text-center mb-12">My Accomplishments</h1>

      {accomplishments.map((card, index) => (
        <div
          key={index}
          className={`flex flex-col md:flex-row items-center ${
            index % 2 !== 0 ? 'md:flex-row-reverse' : ''
          }`}
          data-aos={index % 2 === 0 ? 'fade-right' : 'fade-left'}
        >
          {/* Image/Icon side */}
          <div className="w-full md:w-1/2 h-64 flex items-center justify-center p-4">
            <img
              src={card.image}
              alt="Icon"
              className="h-32 w-32 object-contain transform hover:scale-105 transition duration-300"
            />
          </div>

          {/* Text side */}
          <div
            className="w-full md:w-1/2 p-6 rounded-lg shadow-lg transition duration-300 hover:shadow-xl"
            style={{ backgroundColor: 'rgb(199, 224, 189)' }}
          >
            <h2 className="text-2xl font-semibold text-gray-800 mb-4">Highlights</h2>
            <ul className="list-disc list-inside text-gray-700 space-y-2">
              {card.items.map((item, i) => (
                <li key={i}>{item}</li>
              ))}
            </ul>
          </div>
        </div>
      ))}
    </div>
  );
}
